                                    // Function to check if passwords match and enable/disable the confirm button
                                    function checkPasswords() {
                                        if (passwordField.value !== confirmPasswordField.value) {
                                            confirmbtnField.disabled = true;
                                            confirmbtnField.style.backgroundColor = 'red';

                                        } else {
                                            confirmbtnField.disabled = false;
                                            confirmbtnField.style.backgroundColor = '#2C64FA';
