                                            if (modal) {
                                                modal.style.display = "none";
                                            }
                                        };
                                    }

                                    // Close the modal when the user clicks anywhere outside of the modal
                                    window.onclick = (event) => {
                                        if (modal && event.target === modal) {
                                            modal.style.display = "none";
                                        }
